    local mod:RemoteEvent = RS:FindFirstChild(dig and "Dig" or "Place")
    if dig then
        mod:FireServer(unpack({
            [1] = shovelType,
            [2] = vec3,
        }))
    else
        mod:FireServer(unpack({
            [1] = shovelType,
            [2] = vec3,
            [3] = false
        }))
    end
end

return snow
